char control_css[] = "#control {\n    /* float: left; */\n    width: 52%;\n    overflow-y: auto;\n    overflow-x: hidden;\n    display: block;\n    height: 100%;\n    margin-right: 8px;\n}\n\n.LedRow {\n    /* display: table-cell; */\n    display: inline-flex;\n    align-items: center;\n    margin-left: 8px;\n    padding-bottom: 4px;\n    width: 100%;\n}\n\n.LedBtn {\n    border-radius: 6px;\n    border-width: 1px;\n    /* padding: 12px; */\n    width: 64px;\n    height: 36px;\n    margin-left: 4px;\n    margin-bottom: 8px;\n    background-color: #e0e2ec;\n    color: #43474e;\n    border: none;\n    cursor: pointer;\n    box-shadow: 0px 3px 1px -2px rgba(0, 0, 0, 0.2);\n}\n\n.LedBtn:hover {\n    box-shadow: 1px 4px 8px 2px rgba(0, 0, 0, 0.2);\n}\n\n.LedBtn:active {\n    /* background-color: #d8e3f8; */\n    box-shadow: 0px 1px 5px 0px rgba(0, 0, 0, 0.2);\n    /* transform: translateY(1px); */\n}\n\n.LedBtn:disabled {\n    box-shadow: none;\n    cursor: auto;\n    /* color: inherit; */\n    /* background-color: initial; */\n    /* border: 1px solid; */\n}\n\n\n.RowIndex {\n    /* display: table-cell; */\n    /* float: left; */\n    vertical-align: middle;\n    margin-right: 24px;\n    margin-left: 8px;\n    margin-top: 0px;\n    margin-bottom: 0px;\n\n}\n\n.DIBtn {\n    margin-left: 4px;\n    margin-right: 8px;\n    width: 48px;\n    height: 32px;\n    border-radius: 8px 8px 8px 8px;\n    /* border-width: 1px; */\n    border: 1px solid;\n    /* background-color: #fdfcff; */\n    color: #1a1c1e;\n    margin-bottom: 8px;\n    background: inherit;\n}\n\n.DOBtn {\n    margin-left: 4px;\n    margin-right: 8px;\n    width: 48px;\n    height: 32px;\n    border-radius: 8px 8px 8px 8px;\n    background-color: #e0e2ec;\n    color: #43474e;\n    border: none;\n    cursor: pointer;\n    box-shadow: 0px 3px 1px -2px rgba(0, 0, 0, 0.2);\n    margin-bottom: 8px;\n}\n\n.DOBtn:hover {\n    box-shadow: 1px 4px 8px 2px rgba(0, 0, 0, 0.2);\n}\n\n.DOBtn:active {\n    /* background-color: #d8e3f8; */\n    box-shadow: 0px 1px 5px 0px rgba(0, 0, 0, 0.2);\n    /* transform: translateY(1px); */\n}\n\n\n.DOBtn:disabled {\n    box-shadow: none;\n    cursor: auto;\n    /* color: inherit; */\n    /* background-color: initial; */\n    /* border: 1px solid; */\n}\n\n\n\n.ErrLed {\n    background-color: #ba1a1a;\n    color: #ffffff;\n    /* border: none; */\n}\n\n.YellowLed {\n    background-color: #ffe08a;\n    color: #241a00;\n    /* border: none; */\n}\n\n.RequestYellowLed {\n    background-color: #ffe08a;\n    color: #241a00;\n}\n\n.GreenLed {\n    background-color: #c2f18e;\n    color: #0f2000;\n    /* border: none; */\n}\n\n.ActiveBtn {\n    background-color: #c2f18e;\n    color: #0f2000;\n}";
