char firmware_css[] = "#Firmware {\n    margin-left: 8px;\n    /* float: left; */\n    width: 18%;\n}\n\n.FirmwareSection {\n    margin-left: 8px;\n}\n\n#fwImg {\n    cursor: pointer;\n}\n\n#progBtn {\n    margin-top: 8px;\n    margin-bottom: 16px;\n    background-color: #d4e3ff;\n    color: #001c3a;\n    width: 72px;\n    height: 28px;\n    border: none;\n    border-radius: 6px;\n    cursor: pointer;\n    box-shadow: 0px 3px 1px -2px rgba(0,0,0,0.2);\n}\n\n\n#progBtn:hover {\n    box-shadow: 1px 4px 8px 2px rgba(0,0,0,0.2);\n}\n\n#progBtn:active {\n  background-color: #d8e3f8;\n  box-shadow: 0px 1px 5px 0px rgba(0,0,0,0.2);\n  /* transform: translateY(1px); */\n}";
