char log_css[] = "#log {\n    /* float: left; */\n    width: 28%;\n    /* overflow-y:scroll; */\n    height: 100%;\n    /* height: 95%; */\n    /* margin-left: 8px; */\n\n}\n\n#logContent\n{\n    margin-bottom: 16px;\n    height: 90%;\n    /* height: 90%; */\n    padding: 8px;\n    /* background: #FFFFFF; */\n    /* background: #fafafa; */\n    /* background: #e6e1e6; */\n    background: #EDEDF4;\n    overflow-x: hidden;\n    overflow-y:auto;\n    /* width: 100%; */\n    border-radius: 8px;\n}\n\n.logToolBtn {\n    height: 28px;\n    /* margin-left: 4px; */\n    cursor: pointer;\n    width: 48px;\n    border: none;\n    /* border: 1px solid; */\n    /* border-radius: 8px; */\n    /* background: #e0e2ec; */\n    background: inherit;\n}\n\n.logToolBtn:hover {\n    background: #e0e2ec;\n    /* box-shadow: 1px 4px 8px 2px rgba(0, 0, 0, 0.2); */\n}\n\n.logToolBtn:active {\n    /* background-color: #d8e3f8; */\n    box-shadow: 0px 1px 5px 0px rgba(0, 0, 0, 0.2);\n    /* transform: translateY(1px); */\n}\n\n#logToolBox {\n    /* float: right; */\n    position: absolute;  \n    right: 20px;\n    margin-top: 8px;\n}\n\n#logToolPanel {\n    display: inline-flex;\n}\n\n.LogWarn {\n    /* color: #ffe08a; */\n    color: #ff8d46;\n}\n\n.LogErr {\n    /* color: #ba1a1a; */\n    color: #e42d1f;\n    font-weight: bold;\n}\n\n.LogCritical {\n    /* color: #ffe08a; */\n    color: #ff8d46;\n    font-weight: bold;\n}\n\n#logFilterSelect {\n    height: 32px;\n    margin-left: 4px;\n}";
